import React from 'react';

class Pagination extends React.Component {

    handleClick = (number) => {
        this.props.fetchData(number);
    };

    render() {
        const pageNumbers = [];
        if (this.props.fetchData.total !== null) {
            for (let i = 1; i <= Math.ceil(this.props.fetchData.total / this.props.fetchData.per_page); i++) {
                pageNumbers.push(i);
            }



            var renderPageNumbers = pageNumbers.map(number => {
                let classes = this.props.fetchData.current_page === number;
                return (
                    <span key={number} className={classes} onClick={() => this.handleClick(number)}>{number}</span>
                );

            });
        }


        return (
            <div>
                <span onClick={() => this.handleClick}>&laquo;</span>
                {renderPageNumbers}
                <span onClick={() => this.handleClick}>&raquo;</span>
            </div>
        );
    }
}


export default Pagination;
