import React from 'react';

class Pagination extends React.Component {

    handleClick = () => {
        this.props.fetchData(2);
    };

    render() {
        const pageNumbers = [];
        if (this.props.total !== null) {
          for (let i = 1; i <= Math.ceil(this.props.total / this.props.per_page); i++) {
            pageNumbers.push(i);
          }


          let renderPageNumbers = pageNumbers.map(number => {
            let classes = this.state.current_page === number;

          });
        }


        return (
            <div className={styles.pagination}>
          <span onClick={() => this.makeHttpRequestWithPage(1)}>&laquo;</span>
          {renderPageNumbers}
          <span onClick={() => this.makeHttpRequestWithPage(1)}>&raquo;</span>
        </div>
        );
    }
}


export default Pagination;
