import React from 'react';

class Pagination extends React.Component {

    state = {
        currentPage: 1
    };

    handlePreviousClick = () => {
        const currentPage = this.state.currentPage;
        if(currentPage > 1){
            this.setState({
                currentPage: currentPage - 1
            });
            this.props.fetchData(currentPage - 1);
        }
    }

    handleNextClick = () => {
        const currentPage = this.state.currentPage;
        if(currentPage < Math.ceil(this.props.total / 100)){
            this.setState({
                currentPage: currentPage + 1
            });
            this.props.fetchData(currentPage + 1);
        }
    }

    render() {
        const pageNumbers = [];
        if (this.props.fetchData.total !== null) {
            for (let i = 1; i <= Math.ceil(this.props.total / 100); i++) {
                pageNumbers.push(i);
            }

            var renderPageNumbers = pageNumbers.map(number => {
                if(number === this.state.currentPage){
                    return (
                        <span key={number} onClick={() => this.props.fetchData(number)}><b>{number}</b></span>
                    );
                }
                return (
                    <span key={number} onClick={() => this.props.fetchData(number)}>{number}</span>
                );

            });
        }


        return (

            <div className = 'pagination'>
                <span onClick={() => this.handlePreviousClick()}>&laquo;</span>
                {renderPageNumbers}
                <span onClick={() => this.handleNextClick()}>&raquo;</span>
            </div>
        );
    }
}


export default Pagination;
