import React from 'react';

class Pagination extends React.Component {

    handleClick = () => {
        this.props.fetchData(2);
    };

    render() {
        const pageNumbers = [];
        if (this.props.total !== null) {
          for (let i = 1; i <= Math.ceil(this.props.total / this.props.per_page); i++) {
            pageNumbers.push(i);
          }


          let renderPageNumbers = pageNumbers.map(number => {
            let classes = this.state.current_page === number;

          });
        }


        return (
            <nav aria-label="Page navigation example">
                <ul class="pagination pg-blue">
                    <li class="page-item"><a class="page-link"> Previous</a></li>
                    <li class="page-item" onClick={this.handleClick}><a class="page-link">1</a></li>
                    {renderPageNumbers}
                    <li class="page-item"><a class="page-link">2</a></li>
                    <li class="page-item"><a class="page-link">3</a></li>
                    <li class="page-item"><a class="page-link">Next</a></li>
                </ul>
            </nav>
        );
    }
}


export default Pagination;
