import React, { Component } from 'react';
import Pagination from './Pagination';
import Modal from 'react-modal';

class Table extends Component {

   constructor() {
      super();

      this.state = {
        modalIsOpen: false
      };

      this.openModal = this.openModal.bind(this);
      this.afterOpenModal = this.afterOpenModal.bind(this);
      this.closeModal = this.closeModal.bind(this);
    }


  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }


   renderTableHeader() {
      if (this.props.data.length > 0) {
         let header = Object.keys(this.props.data[0])
         return header.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
         })
      }
      return null;
   }

   renderTableData() {
      return this.props.data.map((data, index) => {
         const { Title, Year, imdbID, Type, Poster } = data 
         return (
            <tr>
               <td onClick = {this.openModal}>{Title}</td>
               <td>{Year}</td>
               <td>{imdbID}</td>
               <td>{Type}</td>
               <td><img src={Poster} width='50' height='80' className='img-responsive' /> </td>
            </tr>
         )
      })
   }



   render() {
      return (
         <div className='container'>
            <h1 id='title'>React Data Table</h1>
            <table className='table table-bordered table-striped' >
               <thead>{this.renderTableHeader()}</thead>
               <tbody>
                  {this.renderTableData()}
               </tbody>
            </table>
            <Pagination fetchData={this.props.fetchData} total={this.props.total}/>
         </div>
      )
   }
}

export default Table
