import React from 'react';
import Table from './Table';


class App extends React.Component {
    state = {
        data: [],
        total: null,
        per_page: null,
        current_page: 1
    }
    render() {
        return (
            <Table data={this.state.data} fetchData={this.fetchData} total={this.state.total} perPage = {this.per_page} currentPage = {this.current_page}/>
        );


    }
    componentDidMount() {
        this.fetchData(1);
    }
    fetchData(pageNumber) {
        var url = "http://www.omdbapi.com/?s=iron&apikey=c7e2df9&page=" + pageNumber;
        fetch(url).then(res => res.json()).then(res => {
            this.setState({ data: res.Search, total: res.totalResults,
                per_page: 10})
        })
    }
}

export default App;
