import React, { Component } from 'react';
import Pagination from './Pagination';
import Modal from './Modal';

class Table extends Component {

   state = {
      modalOpen: false
   }

   openModal = () => {
      this.setState({ modalOpen: true });
   }

   closeModal = () => {
      this.setState({ modalOpen: false });
   }

   renderTableHeader() {
      if (this.props.data.length > 0) {
         let header = Object.keys(this.props.data[0])
         return header.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
         })
      }
      return null;
   }

   renderTableData() {
      return this.props.data.map((data) => {
         const { Title, Year, imdbID, Type, Poster } = data
         return (
            <tr>
               <td onClick={this.openModal}> <a href="#"></a>{Title}</td>
               <td>{Year}</td>
               <td>{imdbID}</td>
               <td>{Type}</td>
               <td><img src={Poster} width='50' height='80' className='img-responsive' /> </td>
            </tr>
         )
      })
   } 3



   render() {
      return (
         <div className='container'>
            <Modal isOpen={this.state.modalOpen} closeModal={this.closeModal} />
            <h1 id='title'>React Data Table</h1>
            <table className='table table-bordered table-striped' >
               <thead>{this.renderTableHeader()}</thead>
               <tbody>
                  {this.renderTableData()}
               </tbody>
            </table>
            <Pagination fetchData={this.props.fetchData} total={this.props.total} />
         </div>
      )
   }
}

export default Table
